import React from "react";

class Opdracht3 extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name: '',
            price: '',
            items: []
        }
    }

    handleInput(e) {
        let { name, value } = e.target;
        if (name === 'price') value = +value;
        this.setState({ [name]: value });
      }
        
    render() {
        return (
            <div>
                <form>
                <input type="text" name="name" onChange={this.handleInput} value={this.state.name} />
                <input type="text" name="price" onChange={this.handleInput} value={this.state.price} />
                <select value={this.state.value} onChange={(e) => this.handleChange(e)}>{this.state.items.map((x,y) => <option key={y}>{x}</option>)}</select>
                </form>
                <p>Input: {this.state.value}</p>
            </div>
        );
      }
}

export default Opdracht3;
