import React from "react";
//import SongListTestB from "./components/SongListTestB";
import FetchAPI from "./components/FetchAPI";
import "./styles.css";

// api call: api.openweathermap.org/data/2.5/weather?q=London,uk
const API_Key = "e23a1892a1d9e6b0d6127c8c55d05352";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      city: undefined,
      country: undefined
    };
    this.getWeather();
  }

  getWeather = async () => {
    const url = `http://api.openweathermap.org/data/2.5/weather?q=Nijmegen,nl&appid=${API_Key}`;
    const response = await fetch(url);
    const data = await response.json();
    console.log(data);

    this.setState({
      city: data.name,
      country: data.sys.country,
      celsius: data.main.temp,
      temp_max: data.main.temp_max,
      temp_min: data.main.temp_min
    });
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <FetchAPI city={this.state.city} country={this.state.country} />
        </header>
      </div>
    );
  }
}

export default App;
