import React from "react";

class Opdracht5 extends React.Component {
    constructor(props) {
    super(props);
        this.state = {
            items: [],
            isLoaded: false,
            searchName: '',
        }
    }

    handleInput = (e) => {
        this.setState({ searchSong: e.target.value })
    }
 
    componentDidMount() {
        fetch('https://jsonplaceholder.typicode.com/users')
            .then(res => res.json())
            .then(json => {
                this.setState({ 
                isLoaded: true,
                items: json
                })
            });
    }

    handleInput = (e) => {
        this.setState({searchName: e.target.value})
    }
    
    render() {
        let filterNames = this.state.items.filter((name) => {
            return name.name.toLowerCase().includes(this.state.searchName.toLowerCase())
        })
        var { isLoaded, items } = this.state;
        
        if(!isLoaded) {
            return <div>Loading...</div>
        } else {
            return (
                <div>
                    <select onChange={this.handleInput}>
                        {items.map((name) =><option key={name} value={name.name}>{name.name}</option>)}
                    </select>
                    <p>Filter op: {this.state.searchName}</p>
                    <table striped bordered hover>
                        <thead>
                            <tr>

                            </tr>
                        </thead>
                        <tbody>
                            <tr>
                                {
                                filterNames.map(item => (
                                    <td key={item.id}>{item.name} {item.email}</td>
                                ))
                                }
                            </tr>
                        </tbody>
                    </table>
                </div>
            );
        }
    }
}

export default Opdracht5;
