import React from "react";

class Opdracht2B1 extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            count: 1,
            text: 'Niks',
            naam: ["Papa", "Mama", "Pietje", "Nick"]
        }
    }

    countPlus = () => {
        // eslint-disable-next-line
        this.setState({count: this.state.count + 1});
        this.setState({text: 'Veranderd'});
    }

    resetClick = () => {
        // eslint-disable-next-line
        this.setState({count: this.state.count = 1});
    }

    render() {
        return (
            <div>
                Count: {this.state.count}
                <p>{this.state.text}</p>
                <br/>
                <input type='button' onClick={() => this.countPlus()} value='Click'/>
                <input type='button' onClick={() => this.resetClick()} value='Reset'/>
            </div>
        );
      }
}

export default Opdracht2B1;
