import React from "react";

class LesOpdracht extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            count: 1,
            naam: ["Papa ", "Mama ", "Pietje ", "Nick"]
        }
    }

    countPlus = () => {
        // eslint-disable-next-line
        this.setState({count: this.state.count + 1});
    }

    resetClick = () => {
        // eslint-disable-next-line
        this.setState({count: this.state.count = 1});
    }

    alertje() {
        alert(`Je klikte op ${this.state.naam}`);
    }

    render() {
        return (
            <div>
                Count: {this.state.count}
                <br/> <br/>
                <input type='button' onClick={() => this.countPlus()} value='Click'/>
                <input type='button' onClick={() => this.resetClick()} value='Reset'/>
                <br/> <br/>
                {
                    this.state.naam.map((naam) => {
                        return (
                        <tr>
                            <li onClick={this.alertje()}>{naam}</li>
                        </tr>
                        );
                    })
                }
            </div>
        );
      }
}

export default LesOpdracht;
