import React from "react";

class Opdracht5 extends React.Component {
    constructor(props) {
        super(props);
     
        this.state = {
          data: null,
        };
      }
     
      componentDidMount() {
        fetch('https://api.mydomain.com')
          .then(response => response.json())
          .then(data => this.setState({ data }));
        console.log(data)
      }
     
}

export default Opdracht5;
