import React from "react";

class FetchAPI extends React.Component {
    constructor() {
        super();
        this.state = {
          city: undefined,
          country: undefined
        };
        this.getWeather();
      }

      celsius(temp) {
          let celsius = Math.floor(temp - 273.15);
          return celsius;
      }

    getWeather = async () => {
        // api call: api.openweathermap.org/data/2.5/weather?q=London,uk
        const API_Key = "e23a1892a1d9e6b0d6127c8c55d05352";
        const url = `http://api.openweathermap.org/data/2.5/weather?q=Nijmegen,nl&appid=${API_Key}`;
        const response = await fetch(url);
        const data = await response.json();
        console.log(data);
    
        this.setState({
          city: data.name,
          country: data.sys.country,
          temp: this.celsius(data.main.temp),
          temp_max: this.celsius(data.main.temp_max),
          temp_min: this.celsius(data.main.temp_min)
        });
      }
    
  render() {
    return (
    <div>
        <center><h2>{this.state.city} - {this.state.country}</h2></center>
        <center><img src="https://lh3.googleusercontent.com/proxy/OPHQvnm3ZrXteGt87MY9ygO0eWAjySSwJEALxtl3pUdp6LEnLPtgxLaOajElE3nfjv0yOdDaOrMouY1vhyoP0RZ9Wf7KeV66X3RbXgCegqFiH_-VpzkJk6E_fK9bPHZ7K4ERXT4W5OFHzUznxg" /></center>
        <center><p class="temp">{this.state.temp}</p></center>
        <p>{this.state.temp_min}{this.state.temp_max}</p>
    </div>
    );
  }
}

export default FetchAPI;
