import React from "react";

class Opdracht5 extends React.Component {
    constructor(props) {
    super(props);
        this.state = {
            items: [],
            isLoaded: false,
            searchName: '',
        }
    }
 
    componentDidMount() {
        fetch('https://jsonplaceholder.typicode.com/users')
            .then(res => res.json())
            .then(json => {
                this.setState({ 
                isLoaded: true,
                items: json
                })
            });
    }

    handleInput = (e) => {
        this.setState({searchName: e.target.value})
    }
    
    render() {
        let filterNames = this.state.items.filter((name) => {
            return name.name.toLowerCase().includes(this.state.searchName.toLowerCase())
        })
        var { isLoaded } = this.state;
        
        if(!isLoaded) {
            return <div>Loading...</div>
        } else {
            return (
                <div>
                    Filter: <input onChange={this.handleInput} type="text"/>
                    <p>Filter op: {this.state.searchName}</p>
                    <ul>
                        {
                        filterNames.map(item => (
                            <li key={item.id}>
                                {item.name} | {item.email} | {item.company.name}
                            </li>
                        ))
                        }
                    </ul>
                </div>
            );
        }
    }
}

export default Opdracht5;
