import React from "react";

class Opdracht3 extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            input: [],
            fruits: ["Mango", "Banana", "Appel", "Orange", "Grape"],
            value: ''
        }
    }

    handleChange(event) {
         // hiermee zorg je er voor dat this.state.value actueel blijft en die zie je in de input
            this.setState({value: event.target.value});
          }
        

    render() {
        return (
            <div>
                <form>
                <input type="text" value={this.state.value} onChange={(e) => this.handleChange(e)} />
                <select value={this.state.value} onChange={(e) => this.handleChange(e)}>
                {
                    this.state.fruits.map((item) => {
                    return {item}
                    })
                }
                </select>
                </form>
                <p>{this.state.value}</p>
            </div>
        );
      }
}

export default Opdracht3;
