import React from "react";
import weerIMG from "../img/weer.png";

class WeatherAPI extends React.Component {
  constructor() {
    super();
    this.state = {
      city: undefined,
      country: undefined,
    };
  }

  updateInput(event) {
    if(document.getElementById("input") === "") {
      document.getElementById("alert").innerHTML = "Niks gevonden.";
    } else {
      this.getWeather(event.target.value);
    }
  }

  celsius(temp) {
    let celsius = Math.floor(temp - 273.15);
    return celsius;
  }

  getWeather = async (city) => {
    // api call: api.openweathermap.org/data/2.5/weather?q=City&appid=API_Key
    const API_Key = "e23a1892a1d9e6b0d6127c8c55d05352";
    const url = `http://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_Key}`;
    // eslint-disable-next-line
    const response = await fetch(url).then(response => response.json()).then(data => {
      this.setState({
        city: data.name,
        country: data.sys.country,
        temp: this.celsius(data.main.temp),
        temp_max: this.celsius(data.main.temp_max),
        temp_min: this.celsius(data.main.temp_min),
        description: data.weather[0].description
      });
      document.getElementById("alert").innerHTML = "";
      console.clear();
    })
    .catch((error) => {
      
      console.log(error);
    })
  };
  

  render() {
    return (
      <div className="container">
        <form>
          <input id="input" type="text" onChange={(e) => this.updateInput(e)}></input>
        </form>
        <p id="alert"></p>
        <h2> {this.state.city} - {this.state.country} </h2>
        <img className="weer" src={weerIMG} alt="weer logo" />
        <p className="temp">Temperatuur: {this.state.temp}&#8451;</p>
        <p className="desc">{this.state.description}</p>
        <p className="min-max"> Min: {this.state.temp_min}&#8451;<br/> Max: {this.state.temp_max}&#8451; </p>
      </div>
    );
  }
}

export default WeatherAPI;
