import React from "react";

class Opdracht2 extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            count: 1,
            text: 'Niks',
            naam: ["Papa", "Mama", "Pietje", "Nick"]
        }
    }

    countPlus() {
        // eslint-disable-next-line
        this.setState({count: this.state.count + 1});
        this.setState({text: 'Veranderd'});
    }

    resetClick() {
        // eslint-disable-next-line
        this.setState({count: this.state.count = 1});
    }

    alertje(data) {
        this.setState({text: data})
        alert(data);
    }

    render() {
        return (
            <div>
                <h3>Opdracht 2</h3>
                Count: {this.state.count}
                <p>{this.state.text}</p>
                <br/>
                <input type='button' onClick={() => this.countPlus()} value='Click'/>
                <input type='button' onClick={() => this.resetClick()} value='Reset'/> <br/><br/>
                {
                    this.state.naam.map((item) => {
                    return <li key={item} onClick={(() => this.alertje(item))}>{item}</li>
                    })
                }
            </div>
        );
      }
}

export default Opdracht2;
