import React from "react";

class SongList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      songs: [
        {
          title: "Perfect",
          artist: "Ed Sheeran",
          link: "https://www.youtube.com/watch?v=emcFmUm5jtQ",
        },
        {
          title: "There is moonshine",
          artist: "Bill Night",
          link: "https://www.youtube.com/watch?v=CYT2z11j-f8",
        },
        {
          title: "Far to Good",
          artist: "John Smith",
          link: "https://www.youtube.com/watch?v=UBczT73O8sg",
        },
      ],
      searchSong: ''
    }
  }

  handleInput = (e) => {
    console.log(e.target.value);
    this.setState({ searchSong: e.target.value })
  }

  render() {
    let filterSongs = this.state.songs.filter((song) => {
      return song.title.toLowerCase().includes(this.state.searchSong.toLowerCase())
    })
    return (
      <div>
        <h3>Song List 1</h3>
        <input onChange={this.handleInput} type="text"/>
        <ul>
        {
          filterSongs.map((song, k) => {
          return <p key={k}>{song.artist} - {song.title}</p>
          })
        }
        </ul>
      </div>
    )
  }
}

export default SongList;
