import React from "react";

class LesOpdracht extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            waarde: "Teller +1",
            teller: 1
        }
    }

    HandleClick = (e) => {
        this.setState( {waarde: "Teller +1"});
        this.setState( {teller: this.state.teller + 1});
    }

    render() {
        return (
            <div>
                Teller: {this.state.teller}
                <br/>
                <input type='button' onClick={() => this.HandleClick()} value='Click'/>
            </div>
        );
      }
}

export default LesOpdracht;
