import React from "react";

class Opdracht5 extends React.Component {
    constructor(props) {
    super(props);
        this.state = {
            items: [],
            isLoaded: false,
            searchName: '',
        }
    }
 
    componentDidMount() {
        fetch('http://newsapi.org/v2/top-headlines?country=us&apiKey=68b65ae89b804c2190df29b1a06a011d')
            .then(res => res.json())
            .then(json => {
                this.setState({ 
                isLoaded: true,
                items: json
                })
            });
    }

    handleInput = (e) => {
        this.setState({searchName: e.target.value})
    }
    
    render() {
        var { isLoaded } = this.state;
        
        if(!isLoaded) {
            return <div>Loading...</div>
        } else {
            return (
                <div>
                    Filter: <input onChange={this.handleInput} type="text"/>
                    <p>Filter op: {this.state.searchName}</p>
                    <ul>
                        {
                        items.map(item => (
                            <li key={item.id}>
                                {item.status} | {item.id} | {item.company.name}
                            </li>
                        ))
                        }
                    </ul>
                </div>
            );
        }
    }
}

export default Opdracht5;
