import React from "react";

class Opdracht5 extends React.Component {
    constructor(props) {
    super(props);
        this.state = {
            items: [],
            isLoaded: false,
        }
    }
 
    componentDidMount() {
        fetch('https://jsonplaceholder.typicode.com/users')
            .then(res => res.json())
            .then(json => {
                this.setState({ 
                isLoaded: true,
                items: json
                })
            });
    }
    
    render() {
        var { isLoaded, items } = this.state;

        if(!isLoaded) {
            return <div>Loading...</div>
        } else {
            return (
                <div>
                    <table striped bordered hover>
                        <thead>
                            <tr>
                                <th>Naam</th>
                                <th>E-Mail</th>
                            </tr>
                        </thead>
                        <tbody>
                            <tr>
                                {
                                items.map(item => (
                                    <td className="lijst" key={item.id}>
                                    {item.name} {item.email}
                                    </td>
                                ))
                                }
                            </tr>
                        </tbody>
                    </table>
                </div>
            );
        }
    }
}

export default Opdracht5;
