import React from "react";

class LesOpdracht extends React.Component {
    constructor(props){
        super(props);
        count: 1
    }

    onclick = (e) => {
           return {count: this.prevState.count++}
    }

    render() {
        return (
            <div>
                Count: {this.state.count}
                <br/>
                <input type='button' onClick={this.onclick.bind()} value='Click'/>
            </div>
        );
      }
}

export default LesOpdracht;
