      }

      render() {
        const { hits } = this.state;
     
        return (
          <ul>
            {hits.map(hit =>
              <li key={hit.objectID}>
                <a href={hit.url}>{hit.title}</a>
              </li>
            )}
          </ul>
        );
      }
     
}

export default Opdracht5;
