import React from "react";

class Opdracht5 extends React.Component {
    constructor(props) {
    super(props);
        this.state = {
            items: [],
            isLoaded: false,
        }
    }
 
    componentDidMount() {
        fetch('https://jsonplaceholder.typicode.com/users')
            .then(res => res.json())
            .then(json => {
                this.setState({ 
                isLoaded: true,
                items: json
                })
            });
    }
    
    render() {
        return (
            <div>

            </div>
        );
    }
}

export default Opdracht5;
