import React from "react";

class Opdracht5 extends React.Component {
    constructor(props) {
    super(props);
        this.state = {
            items: [],
            isLoaded: false,
        }
    }

    handleInput = (e) => {
        this.setState({ searchSong: e.target.value })
    }
 
    componentDidMount() {
        fetch('https://jsonplaceholder.typicode.com/users')
            .then(res => res.json())
            .then(json => {
                this.setState({ 
                isLoaded: true,
                items: json
                })
            });
    }
    
    render() {
        var { isLoaded, items } = this.state;
        
        if(!isLoaded) {
            return <div>Loading...</div>
        } else {
            return (
                <div>
                    <select onChange={this.handleInput}>
                        {this.state.items.map((item) =><option key={item} value={item.name}>{item.name}</option>)}
                    </select>
                    <table striped bordered hover>
                        <thead>
                            <tr>

                            </tr>
                        </thead>
                        <tbody>
                            <tr>
                                {
                                items.map(item => (
                                    <td key={item.id}>{item.name} {item.email} </td>
                                ))
                                }
                            </tr>
                        </tbody>
                    </table>
                </div>
            );
        }
    }
}

export default Opdracht5;
