import React from "react";

class Opdracht4 extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      songs: [
        {
          id: "0",
          title: "Perfect",
          artist: "Ed Sheeran",
          link: "https://www.youtube.com/watch?v=emcFmUm5jtQ",
        },
        {
          id: "1",
          title: "Shape of You",
          artist: "Ed Sheeran",
          link: "https://www.youtube.com/watch?v=emcFmUm5jtQ",
        },
        {
          id: "2",
          title: "There is moonshine",
          artist: "Bill Night",
          link: "https://www.youtube.com/watch?v=CYT2z11j-f8",
        },
        {
          id: "3",
          title: "Far to Good",
          artist: "John Smith",
          link: "https://www.youtube.com/watch?v=UBczT73O8sg",
        },
      ],
      searchSong: ''
    }
  }

  handleInput = (e) => {
    this.setState({searchSong: e.target.value})
  }

  render() {
    let filterSongs = this.state.songs.filter((song) => {
      return song.artist.toLowerCase().includes(this.state.searchSong.toLowerCase())
    })
    return (
      <div>
        <h3>Opdracht 4</h3>
        <select onChange={this.handleInput}>
          {this.state.songs.map((song) =><option key={song.id} value={song.artist}>{song.artist}</option>)}
        </select>
        <ul>
        {
          filterSongs.map((song, k) => {
          return <li key={k}>{song.artist} - {song.title}</li>
          })
        }
        </ul>
      </div>
    )
  }
}

export default Opdracht4;
