import React from "react";

class LesOpdracht extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            count: 1
        }
    }

    onClick (e) {
        this.setState( {count: this.state.count + 1});
    }

    render() {
        return (
            <div>
                Count: {this.state.count}
                <br/>
                <input type='button' onClick={this.onClick()} value='Click'/>
            </div>
        );
      }
}

export default LesOpdracht;
