import React from "react";
import weerIMG from "../img/weer.png";

class WeatherAPI extends React.Component {
  constructor() {
    super();
    this.state = {
      city: undefined,
      country: undefined,
      newCity : '',
    };
    this.updateInput = this.updateInput.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  updateInput(event){
    console.log(event.target.value);
    return event.target.value;
  }
  handleSubmit(){
    this.getWeather();
    //Send state to the server code
  }

  celsius(temp) {
    let celsius = Math.floor(temp - 273.15);
    return celsius;
  }

  getWeather = async () => {
    // api call: api.openweathermap.org/data/2.5/weather?q=City&appid=API_Key
    const API_Key = "e23a1892a1d9e6b0d6127c8c55d05352";
    let City = this.updateInput();
    const url = `http://api.openweathermap.org/data/2.5/weather?q=${City}&appid=${API_Key}`;
    const response = await fetch(url);
    const data = await response.json();
    console.log(data);

    this.setState({
      city: data.name,
      country: data.sys.country,
      temp: this.celsius(data.main.temp),
      temp_max: this.celsius(data.main.temp_max),
      temp_min: this.celsius(data.main.temp_min),
      icon: data.weather.icon
    });
  };
  

  render() {
    return (
      <div class="container">
        <div class="bg"></div>
        <form>
          <input type="text" onChange={this.updateInput}></input>
          <input type="submit" onClick={this.getWeather} ></input>
        </form>
        <h2> {this.state.city} - {this.state.country} </h2>
        <img class="weer" src={weerIMG} alt="weer logo" />
        <p class="temp">Gemidelde: {this.state.temp}&#8451;</p>
        <p class="min-max"> Min: {this.state.temp_min}&#8451;<br/> Max: {this.state.temp_max}&#8451; </p>
        <p>{this.state.newCity}</p>
      </div>
    );
  }
}

export default WeatherAPI;
