import React from "react";
import weerIMG from "../img/weer.png";

class WeatherAPI extends React.Component {
  constructor() {
    super();
    this.state = {
      city: undefined,
      country: undefined,
    };
  }

  updateInput = (event) => {
    if (event.target.value === ""){
      document.getElementById("alert").innerHTML = "";
    } else {
      this.getWeather(event.target.value);
      hideStatus = () => {
        document.getElementById("status").style.display = "block";
        document.getElementById("description").style.display = "block";
      }
    }
  }

  celsius(temp) {
    let celsius = Math.floor(temp - 273.15);
    return celsius;
  }

  getWeather = async (city) => {
    // api call: api.openweathermap.org/data/2.5/weather?q=City&appid=API_Key
    const API_Key = "e23a1892a1d9e6b0d6127c8c55d05352";
    const url = `http://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_Key}`;
    // eslint-disable-next-line
    const response = await fetch(url).then(response => response.json()).then(data => {
      this.setState({
        city: data.name,
        country: data.sys.country,
        temp: this.celsius(data.main.temp),
        temp_max: this.celsius(data.main.temp_max),
        temp_min: this.celsius(data.main.temp_min),
        description: data.weather[0].description
      });
      document.getElementById("alert").innerHTML = "";
      console.clear();
    })
    .catch((error) => {
      document.getElementById("alert").innerHTML = "Niks gevonden.";
      console.log(error);
      this.setState({
        city: null,
        country: null,
        temp: null,
        temp_max: null,
        temp_min: null,
        description: null
      })
    })
  };

 
  

  render() {
    return (
      <div>
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
          <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav">
              <li className="nav-item">
                <a className="nav-link" href="http://localhost:3000/">Home</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="http://localhost:3000/">Info</a>
              </li>
            </ul>
          </div>
        </nav>
        <div className="container">
          <div className="row">
            <div className="col-12">
              <img className="weer" src={weerIMG} alt="weer logo" />
              <div className="head">
                <h1>React Weather App</h1>
                <h5>Made by: <small>Joost & Batuhan</small></h5>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-12">
              <form>
                <input type="text" className="form-control" placeholder="Voer een plaats in" onChange={(e) => this.updateInput(e)}></input>
              </form>
              <div className="informatie">
                <button onClick={this.hideStatus} type="button" className="btn btn-info">Meer informatie</button>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-12">
              <p id="alert"></p>
              <h2> {this.state.city} - {this.state.country} </h2>
              <p className="temp">Temperatuur: {this.state.temp}&#8451;</p>
              <table className="table">
                <thead>
                  <tr>
                    <th scope="col">Min</th>
                    <th scope="col">Max</th>
                    <th id="status" scope="col">Status</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td>{this.state.temp_min}&#8451;</td>
                    <td>{this.state.temp_max}&#8451;</td>
                    <td id="description" className="desc">{this.state.description}</td>
                  </tr>
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default WeatherAPI;
